from graph_creator.services.json_handler import transform_llm_output_to_dict


def test_valid_json():
    llm_output = '[{"key1": "value1", "key2": "value2"}]'
    expected_output = [{"key1": "value1", "key2": "value2"}]
    assert transform_llm_output_to_dict(llm_output) == expected_output


def test_leading_comments_and_text():
    llm_output = """
    Here is the requested json:
    // This is a comment
    [
        {"key1": "value1", "key2": "value2"}
    ]
    """
    expected_output = [{"key1": "value1", "key2": "value2"}]
    assert transform_llm_output_to_dict(llm_output) == expected_output


def test_trailing_text():
    llm_output = '[{"key1": "value1", "key2": "value2"}] Some trailing text.'
    expected_output = [{"key1": "value1", "key2": "value2"}]
    assert transform_llm_output_to_dict(llm_output) == expected_output


def test_double_commas():
    llm_output = '[{"key1": "value1", "key2": "value2",, "key3": "value3"}]'
    expected_output = [{"key1": "value1", "key2": "value2", "key3": "value3"}]
    assert transform_llm_output_to_dict(llm_output) == expected_output


def test_comments_within_json():
    llm_output = """
    [
        {"key1": "value1", "key2": "value2" /* inline comment */, "key3": "value3"},
        // single line comment
        {"key1": "value4", "key2": "value5", "key3": "value6"}
    ]
    """
    expected_output = [
        {"key1": "value1", "key2": "value2", "key3": "value3"},
        {"key1": "value4", "key2": "value5", "key3": "value6"},
    ]
    assert transform_llm_output_to_dict(llm_output) == expected_output


def test_extra_commas_before_closing_brackets():
    llm_output = '[{"key1": "value1", "key2": "value2",}]'
    expected_output = [{"key1": "value1", "key2": "value2"}]
    assert transform_llm_output_to_dict(llm_output) == expected_output


def test_incorrect_json_structure():
    llm_output = "Here is the requested json: [key1: value1, key2: value2]"
    expected_output = {}
    assert transform_llm_output_to_dict(llm_output) == expected_output


def test_empty_input():
    llm_output = ""
    expected_output = {}
    assert transform_llm_output_to_dict(llm_output) == expected_output


def test_no_json_found():
    llm_output = "Here is the requested json: no json here."
    expected_output = {}
    assert transform_llm_output_to_dict(llm_output) == expected_output


